// Parse the Claude response and extract the JSON array
const response = $input.first().json;

let content = response.message?.content || response.text || response.content || '';

// If content is an array (Anthropic format), extract text
if (Array.isArray(content)) {
  content = content.map(c => c.text || c).join('');
}

// Clean up the response - remove markdown code blocks if present
content = content.toString().trim();
if (content.startsWith('```json')) {
  content = content.slice(7);
}
if (content.startsWith('```')) {
  content = content.slice(3);
}
if (content.endsWith('```')) {
  content = content.slice(0, -3);
}
content = content.trim();

// Find the JSON array in the response
const jsonMatch = content.match(/\[\s*\{[\s\S]*\}\s*\]/);
if (!jsonMatch) {
  throw new Error('Could not find valid JSON array in response');
}

const variations = JSON.parse(jsonMatch[0]);

// Validate the structure
if (!Array.isArray(variations) || variations.length !== 5) {
  throw new Error('Expected exactly 5 variations');
}

for (const v of variations) {
  if (!v.platform || !v.copy || !v.image_prompt) {
    throw new Error('Each variation must have platform, copy, and image_prompt');
  }
}

return [{ json: { variations } }];
